package it.MadDiscord.Control;

import java.io.IOException;
import java.io.InputStream;

import javax.servlet.RequestDispatcher;
import javax.servlet.ServletException;
import javax.servlet.annotation.MultipartConfig;
import javax.servlet.annotation.WebServlet;
import javax.servlet.http.*;

import java.io.ByteArrayOutputStream;
import java.sql.SQLException;
import java.util.Base64;

import it.MadDiscord.Model.ArticleBean;
import it.MadDiscord.Model.ArticleModelDM;


@WebServlet("/Article")
@MultipartConfig(fileSizeThreshold = 1024 * 1024 * 2, // 2MB after which the file will be
// temporarily stored on disk
maxFileSize = 1024 * 1024 * 10, // 10MB maximum size allowed for uploaded files
maxRequestSize = 1024 * 1024 * 50) // 50MB overall size of all uploaded files

public class ArticleServlet extends HttpServlet {
	private static final long serialVersionUID = 1L;
 
    public ArticleServlet() {
        super();
    }

    ArticleModelDM aModel = new ArticleModelDM();
  
    
	protected void doPost(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {
		String sort = request.getParameter("sort");
		
		String action = request.getParameter("action");
		
		try {
		if(action!=null) {
			switch (action) {
		case "insert": {
			
		try (ByteArrayOutputStream bos = new ByteArrayOutputStream()){
			

				String ti = request.getParameter("titolo");
				String cont = request.getParameter("cont");
				
				Part part = request.getPart("immagine");
				InputStream fis = null;
				for(Part p : request.getParts()) {
					System.out.println(p.getName());
				}
								
				fis = part.getInputStream();

				System.out.println("immagine presa");
				byte[] buf = new byte[4096];
				
				for(int rNum;(rNum = fis.read(buf))!=-1;) {
					bos.write(buf, 0, rNum);
					System.out.println("read" + rNum + "bytes");
				}
				
				byte[] bytes = bos.toByteArray();
				
				String img="";
				if(bytes.length>0) {
					img="data:image/jpeg;base64, " + Base64.getEncoder().encodeToString(bytes);
					System.out.println("bytes immagine "+ bytes.length);
				}
				
				else {
					img="data:image/png;base64,";
					
				}
				ArticleBean aBean = new ArticleBean();
				
				aBean.setTitolo(ti);
				aBean.setImmagine(img);
				aBean.setCont(cont);
				
				aModel.doSave(aBean);
				request.setAttribute("message", "Articolo"+aBean.getTitolo()+"salvato");
				
		} 
		}
		case "details": {
			
					String id_articolo = request.getParameter("id_articolo");
					
					request.removeAttribute("article");
					request.setAttribute("article", aModel.doRetrieveBy(id_articolo));
				
		}
		case "delete":	{
			
			
			try {
				String id = request.getParameter("id");
				ArticleBean aBean = aModel.doRetrieveBy(id);
				
				if(aBean!=null && aBean.isEmpty()) {
					aModel.doDelete(aBean);
					request.setAttribute("message", "Prodotto " + aBean.getTitolo() + "rimosso con successo");
				}
				
				
			} catch (SQLException e) {
			
				e.printStackTrace();
			}
			
		}
		case "update" : {
				try (ByteArrayOutputStream bos = new ByteArrayOutputStream()){
			
			 
				
				String ti = request.getParameter("titolo");
				String cont = request.getParameter("cont");
				
				Part part = request.getPart("immagine");
				InputStream fis = null;
				fis = part.getInputStream();
				System.out.println("immagine presa");
				byte[] buf = new byte[4096];
				
				for(int rNum;(rNum = fis.read(buf))!=-1;) {
					bos.write(buf, 0, rNum);
					System.out.println("read" + rNum + "bytes");
				}
				
				byte[] bytes = bos.toByteArray();
				
				String img="";
				if(bytes.length>0) {
					img="data:image/jpeg;base64, " + Base64.getEncoder().encodeToString(bytes);
					System.out.println("bytes immagine "+ bytes.length);
				}
				
				else {
					img="data:image/png;base64,";
					
				}
				ArticleBean aBean = new ArticleBean();
				
				aBean.setTitolo(ti);
				aBean.setImmagine(img);
				aBean.setCont(cont);
				
				aModel.doUpdate(aBean);
				request.setAttribute("message", "Articolo"+aBean.getTitolo()+"salvato");
				} 
			
		}
		case "show" :{
			RequestDispatcher rd = getServletContext().getRequestDispatcher("/ArticleServlet");
			rd.forward(request, response);
		}
			
			
	}}
		} catch (SQLException |NumberFormatException e) {
			System.out.println("Error: "+e.getMessage());
			request.setAttribute("error", e.getMessage());
		}
		
		try {
			request.removeAttribute("articles");
			request.setAttribute("articles", aModel.doRetrieveAll(sort));
			
		} catch (SQLException e) {
			System.out.println("Error: "+e.getMessage());
			request.setAttribute("error", e.getMessage());
		}
		
		RequestDispatcher dispatcher = this.getServletContext().getRequestDispatcher("/gestioneArticoli.jsp");
		dispatcher.forward(request, response);
	}
	
	protected void doGet(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {
		// TODO Auto-generated method stub
		doPost(request, response);
	}

}
